import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from 'axios'
export default function 
() {
    const url = "http://localhost:8080/api/v3/login"
    const [username, setUsername] = useState('');
    const [pass, setPass] = useState('');
    const navigate = useNavigate();

    const handleSubmit = () => {
        console.log(username);
        console.log(pass);
        axios.post(url,{id:username,password:pass})
        .then(response => {
          console.log(response);
          if(response.data == 'sucess')
            navigate("/");
        })
        .catch(error=>{console.log(error)});
        console.log("sent");
    }
    return (
      <div>
          <center>
            <div style={{"padding-top":"100px"}}>
              <h1>Loan Management Application</h1>
              <br/>
            </div>

          <form style={{"padding-left":"35%","padding-right":"35%", "padding-top":"2%"}}>
              <div style={{"border":"1px solid black", "padding":"5% 5% 5%"}}>
              <h3>User Login</h3>
              <br/>
              <div class="form-outline mb-4 form-group">
                <input type="text" id="form2Example1" 
                onChange={(e)=>setUsername(e.target.value)}
                  class="form-control" placeholder="Enter User-ID" required/>
              </div>

              <div class="form-outline mb-4 form-group">
              <input type="password" 
               onChange={(e)=>setPass(e.target.value)}
               id="form2Example2" class="form-control" placeholder="Enter Password"  required/>
              </div>
              <button type="button" class="btn btn-dark btn-block mb-4" onClick={text_setting}>Login</button>
              <div class="text-center">
                <p>Not a member? <a href="/register">Register</a></p>
              </div></div>
          </form>
          </center>
      </div>
  )
}
