import React from 'react';
import { useEffect, useState } from "react";
import { getAll } from './api';
import { Link, useNavigate } from 'react-router-dom';


function EmployeeList(){
    const navigate = useNavigate();
    const [employees, setEmployees] = useState([]);
    function formatDate(dateString) {
      const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
      const date = new Date(dateString);
      return date.toLocaleDateString('en-GB', options);
    }
    useEffect(() => {
        return async () => {
        const response = await getAll();
        setEmployees(response);
        console.log("This is from useEffect", response);
        };
    }, []);
    const handleDelete=(id)=>{
        navigate(`/admin/delete/${id}`);
    }
    const navigateUpdate=(id)=>{
        navigate(`/admin/update/${id}`);
    }
    return(
        <div>
            <center>
                <div className='container' style={{"padding-top":"7%"}}>
                    <div><h1>Admin Dashboard</h1></div><br/>
                    <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>Employee Id</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Designation</th>
            <th>Department</th>
            <th>Email</th>
            <th>Date of Birth</th>
            <th>Date of Joining</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
                <td>{employee.id}</td>
              <td>{employee.firstName}</td>
              <td>{employee.lastName}</td>
              <td>{employee.designation}</td>
              <td>{employee.deparment}</td>
              <td>{employee.emailId}</td>
              <td>{formatDate(employee.dob)}</td>
              <td>{formatDate(employee.doj)}</td>
              <td>
  <div style={{ display: 'flex', gap: '10px' }}>
    <Link to={`../admin/update/${employee.id}`} className="btn btn-outline-warning">
      Update
    </Link>
    <button
      style={{
        padding: '10px',
        border: '2px solid red', // Red outline
      }}
      className='btn btn-outline-danger'
      onClick={() => handleDelete(employee.id)}
    >
      Delete
    </button>
  </div>
</td>

              
            </tr>
          ))}
        </tbody>
      </table>

                </div>
                <br/>


            </center>
        </div>
    )
}

export default EmployeeList;
